import React from "react";
import "./FormComponents.css";

export const Input = ({
  type,
  id,
  required,
  additionalClass = "",
  name,
  placeholder,
  manipulationFunction,
  value,
}) => {
  return (
    <input
      type={type}
      id={id}
      name={name}
      value={value}
      required={required}
      className={`input-component ${additionalClass}`}
      placeholder={placeholder}
      onChange={manipulationFunction}
      autoComplete="off"
    />
  );
};

export const Button = ({
  textButton,
  id,
  name,
  type,
  additionalClass = "",
  manipulationFunction,
}) => {
  return (
    <button
      type={type}
      name={name}
      id={id}
      className={`button-component ${additionalClass}`}
      onClick={manipulationFunction}
    >
      {textButton}
    </button>
  );
};

// const dados = [
//   { value: "12349", text: "Vestu치rio Masculino" },
//   { value: "09876", text: "Vestu치rio Feminino" },
// ];

export const Select = ({ 
    dados = [],
    id,
    name,
    required,
    additionalClass = "",
    manipulationFunction,
    defaultValue
}) => {
  return (
    <select 
        id={id} 
        name={name} 
        required={required}
        className={`input-component ${additionalClass}`}
        onChange={manipulationFunction}
        value={defaultValue}
    >
      <option value="">Selecione</option>
      {dados.map((opt) => {
        return <option key={opt.value} value={opt.value}>{opt.text}</option>;
      })}
    </select>
  );
};
